INSERT INTO `chuyendoicongchung` (`ID_DT`, `TIEUDE_DT`, `NOIDUNG_DT`, `HINHANH_DT`, `NGAYDANG_DT`) VALUES
	(31, 'Ngành dược chuyển đổi số, người dân hưởng nhiều lợi ích', '<h2>(NLĐO) &ndash; Hội nghị Khoa học Kỹ thuật Dược lần thứ 38 tổ chức v&agrave;o chiều 8-4 tại Trường Đại học Y dược TP HCM với nhiều nội dung li&ecirc;n quan đến ứng dụng c&ocirc;ng nghệ y tế nhằm ph&aacute;t triển ng&agrave;nh dược.</h2>\r\n\r\n<p>Tại hội nghị, thạc sĩ - dược sĩ Trương Văn Đạt, Khoa Dược, Trường ĐH Y dược TP HCM, cho biết khi dịch Covid-19 xảy ra, người bệnh gặp nhiều kh&oacute; khăn khi mua thuốc v&agrave; gặp dược sĩ để được tư vấn, hướng dẫn. Đặc biệt, nhu cầu cần được tư vấn hướng dẫn sử dụng c&aacute;c thuốc trong t&uacute;i thuốc F0 d&agrave;nh cho người mắc bệnh Covid-19 l&agrave; rất lớn nhưng chưa đủ nh&acirc;n lực để thực hiện việc n&agrave;y. Do đ&oacute;, việc ứng dụng c&aacute;c giải ph&aacute;p chuyển đổi số như hệ thống tư vấn từ xa, giao thuốc tận nơi... rất quan trọng.</p>\r\n\r\n<p><img alt="Ngành dược chuyển đổi số, người dân hưởng nhiều lợi ích - Ảnh 1." src="https://nld.mediacdn.vn/thumb_w/684/291774122806476800/2022/4/8/z3325968518060d44ee7dd7cd12392a9b734af12f7eff2-1649417545452875619220.jpg" title="Ngành dược chuyển đổi số, người dân hưởng nhiều lợi ích - Ảnh 1." /></p>\r\n\r\n<p>Thạc sĩ - dược sĩ Trương Văn Đạt, Khoa Dược, Trường ĐH Y dược TP HCM, b&aacute;o c&aacute;o tại hội nghị</p>\r\n\r\n<p>Ngo&agrave;i ra, theo dược sĩ Đạt, nhờ chuyển đổi số m&agrave; c&aacute;c c&ocirc;ng ty dược phẩm đ&atilde; thực hiện c&aacute;c nghi&ecirc;n cứu, quản l&yacute; hệ thống sản xuất, ph&acirc;n phối vắc-xin ngừa Covid-19 v&agrave; c&aacute;c loại thuốc đặc trị Covid-19 nhanh ch&oacute;ng.</p>\r\n\r\n<p>Việc chuyển đổi số rất quan trọng, gi&uacute;p dược sĩ c&oacute; thể l&agrave;m việc từ xa, th&ocirc;ng qua mạng m&aacute;y t&iacute;nh, robot... Ngay cả việc thử nghiệm l&acirc;m s&agrave;ng cũng c&oacute; thể thực hiện từ xa từ đ&oacute; đẩy mạnh việc ph&aacute;t minh, sản xuất thuốc, vắc-xin bảo đảm nguồn cung ứng kịp thời.</p>\r\n\r\n<p>Cũng tại hội nghị, GS-TS Trần Th&agrave;nh Đạo, Trưởng Khoa Dược, trường ĐH Y dược TP HCM, Trưởng Ban tổ chức, cho biết th&ecirc;m TP HCM đang c&oacute; định hướng ph&aacute;t triển c&ocirc;ng nghiệp dược trong v&ograve;ng 5 năm. C&ugrave;ng với đ&oacute; l&agrave; mong muốn đưa th&ecirc;m nhiều loại thuốc v&agrave;o danh mục bảo hiểm y tế để được đơn vị bảo hiểm chấp nhận chi trả điều trị cho người bệnh.</p>\r\n\r\n<p><img alt="Ngành dược chuyển đổi số, người dân hưởng nhiều lợi ích - Ảnh 2." src="https://nld.mediacdn.vn/thumb_w/684/291774122806476800/2022/4/8/z332596782846051109b84b469ad6ff4e8a7a7b60918aa-16494174821581482366542.jpg" title="Ngành dược chuyển đổi số, người dân hưởng nhiều lợi ích - Ảnh 2." /></p>\r\n\r\n<p>GS-TS Trần Th&agrave;nh Đạo - Trưởng Khoa Dược, Trường ĐH Y dược TP HCM, Trưởng Ban tổ chức hội nghị - ph&aacute;t biểu khai mạc</p>\r\n\r\n<p>&quot;Muốn l&agrave;m được điều n&agrave;y phải c&oacute; c&ocirc;ng nghệ đ&aacute;nh gi&aacute;, c&aacute;c nh&agrave; nghi&ecirc;n cứu dược phải định hướng tương lai danh mục h&oacute;a của tổ chức bảo hiểm y tế với mục ti&ecirc;u cuối c&ugrave;ng nhằm ph&aacute;t triển ng&agrave;nh dược v&agrave; phục vu người d&acirc;n tốt nhất&quot; &ndash; GS Trần Th&agrave;nh Đạo chia sẻ.</p>\r\n', '/chuyendoicongchung/155bba622a98d21a5663dc2d17ad798e.jpg', '2022-12-01'),
	(33, 'Chuyển đổi số trong ngành dược, bài học từ Anh quốc', '<p>VietTimes &ndash;&nbsp;Đ&atilde; c&oacute; 800 nh&agrave; thuốc tại Anh buộc phải&nbsp;đ&oacute;ng cửa v&igrave; c&aacute;c vấn đề về chi ph&iacute; vận h&agrave;nh&nbsp;được ghi nhận kể từ năm 2015. Nhu cầu chuyển đổi số ng&agrave;nh dược trở n&ecirc;n cần thiết hơn bao giờ hết.</p>\r\n\r\n<p>Những hiệu thuốc mặt tiền từng l&agrave; trụ cột của mọi con phố thượng lưu ở&nbsp;<a href="https://viettimes.vn/anh-quoc-tag19135.html">Anh quốc</a>&nbsp;ng&agrave;y c&agrave;ng hiếm hoi. Trong 7 năm qua, chi ph&iacute; vận h&agrave;nh cao, chi ph&iacute; thu&ecirc; mặt bằng v&agrave; sự thay đổi trong th&oacute;i quen của người ti&ecirc;u d&ugrave;ng đ&atilde; khiến hơn 800 hiệu thuốc phải đ&oacute;ng cửa kể từ năm 2015, một xu hướng kh&ocirc;ng c&oacute; dấu hiệu đảo ngược.</p>\r\n\r\n<p>C&aacute;c nh&agrave; nghi&ecirc;n cứu từ Đại học College London đ&atilde; ước t&iacute;nh rằng đến năm 2024, c&oacute; thể sẽ c&oacute; tới 3000 hiệu thuốc ở Anh phải đ&oacute;ng cửa. V&agrave; khi c&aacute;c nh&agrave; thuốc đ&oacute;ng cửa, một số bệnh nh&acirc;n sẽ rất kh&oacute; khăn trong việc tiếp cận c&aacute;c dịch vụ thiết yếu m&agrave; dược sĩ v&agrave; c&aacute;c nh&agrave; thuốc cung cấp. C&aacute;c nh&oacute;m bệnh nh&acirc;n bao gồm người gi&agrave; v&agrave; những người kh&ocirc;ng c&oacute; phương tiện di chuyển qu&atilde;ng đường d&agrave;i sẽ bị ảnh hưởng nếu số lượng hiệu thuốc đ&oacute;ng cửa ng&agrave;y c&agrave;ng tăng.</p>\r\n\r\n<p>Để giảm bớt kh&oacute; khăn do sự hiện diện ng&agrave;y c&agrave;ng thu hẹp của hiệu thuốc, hệ thống y tế của Anh đang trong qu&aacute; tr&igrave;nh th&iacute;ch ứng v&agrave; đổi mới. Chuyển đổi số l&agrave; trung t&acirc;m của qu&aacute; tr&igrave;nh n&agrave;y, với cơ sở hạ tầng mới được tạo ra để quản l&yacute; bệnh nh&acirc;n bằng c&aacute;c phương tiện kh&ocirc;ng bị rằng buộc v&agrave;o một địa điểm cụ thể. Mặc d&ugrave; sự chuyển đổi n&agrave;y c&ograve;n l&acirc;u mới ho&agrave;n th&agrave;nh, nhưng n&oacute; đang đi đ&uacute;ng hướng để mang lại lợi &iacute;ch cho c&aacute;c nh&agrave; cung cấp dược phẩm v&agrave; cho c&aacute;c bệnh nh&acirc;n.</p>\r\n\r\n<h2>L&yacute; do đằng sau việc số lượng c&aacute;c hiệu thuốc đ&oacute;ng cửa ng&agrave;y một tăng l&ecirc;n?</h2>\r\n\r\n<p>Những l&yacute; do đằng sau h&agrave;ng loạt vụ đ&oacute;ng cửa hiệu thuốc gần đ&acirc;y rất phức tạp, nhưng &aacute;p lực t&agrave;i ch&iacute;nh v&agrave; nhu cầu thay đổi của người ti&ecirc;u d&ugrave;ng l&agrave; những l&yacute; do h&agrave;ng đầu.</p>\r\n\r\n<p>Điều h&agrave;nh một doanh nghiệp dược c&oacute; l&atilde;i đ&ograve;i hỏi phải đạt được sự c&acirc;n bằng hợp l&yacute; giữa c&aacute;c loại thuốc v&agrave; dịch vụ được cung cấp cũng như chi ph&iacute; cung cấp c&aacute;c loại thuốc v&agrave; dịch vụ n&agrave;y. Nhưng khi nguồn t&agrave;i trợ của ch&iacute;nh phủ bị thu hẹp v&agrave; chi ph&iacute; của mọi thứ, từ lương nh&acirc;n vi&ecirc;n đến hệ thống sưởi ấm cửa h&agrave;ng tăng theo cấp số nh&acirc;n, c&aacute;c chủ hiệu thuốc phải đối mặt với những lựa chọn kh&oacute; khăn. B&agrave;i to&aacute;n tiết kiệm chi ph&iacute; ở c&aacute;c hiệu thuốc cần phải được thực hiện, bằng c&aacute;ch ngừng cung cấp một số dịch vụ nhất định hoặc bằng c&aacute;ch t&igrave;m ra những phương thức hiệu quả hơn để cung cấp ch&uacute;ng.</p>\r\n\r\n<p>Y&ecirc;u cầu của c&aacute;c bệnh nh&acirc;n đối với dịch vụ ở c&aacute;c hiệu thuốc cũng đ&atilde; thay đổi. Bệnh nh&acirc;n muốn sự thuận tiện v&agrave; linh hoạt khi y&ecirc;u cầu v&agrave; đặt thuốc. Ở thời điểm hiện tại, việc sử dụng c&ocirc;ng nghệ để gi&uacute;p cuộc sống trở n&ecirc;n dễ d&agrave;ng hơn l&agrave; điều b&igrave;nh thường v&agrave; lĩnh vực dược phẩm cũng kh&ocirc;ng tr&aacute;nh khỏi sự thay đổi n&agrave;y. C&aacute;c biện ph&aacute;p kỹ thuật số đ&atilde; được &aacute;p dụng để gi&uacute;p c&aacute;c bệnh nh&acirc;n mua những loại thuốc m&agrave; họ cần một c&aacute;ch nhanh v&agrave; thuận tiện hơn.</p>\r\n\r\n<h2>Chuyển đổi kỹ số trong ng&agrave;nh dược</h2>\r\n\r\n<p>Một trong những ứng dụng quan trọng của nh&agrave; thuốc số l&agrave; hỗ trợ liền mạch quy tr&igrave;nh tư vấn từ k&ecirc; đơn đến ph&acirc;n phối thuốc.</p>\r\n\r\n<p>Với c&aacute;c nền tảng số ph&ugrave; hợp, bệnh nh&acirc;n c&oacute; thể tham gia c&aacute;c buổi tư vấn trực tuyến với b&aacute;c sĩ, biết được những loại thuốc m&agrave; m&igrave;nh cần v&agrave; được gửi đến tận nh&agrave; bệnh nh&acirc;n. Quy tr&igrave;nh n&agrave;y c&oacute; thể được ho&agrave;n th&agrave;nh trong v&agrave;i giờ, nhanh hơn rất nhiều so với c&aacute;ch l&agrave;m truyền thống.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, điều cần thiết l&agrave; c&aacute;c hiệu thuốc &aacute;p dụng chuyển đổi số phải được t&iacute;ch hợp đ&uacute;ng c&aacute;ch - tức l&agrave; ch&uacute;ng c&oacute; thể &#39;n&oacute;i chuyện&#39; v&agrave; chia sẻ th&ocirc;ng tin với c&aacute;c hệ thống hiện c&oacute; - để giảm thiểu sự chậm trễ trong việc k&ecirc; đơn v&agrave; giao thuốc cho bệnh nh&acirc;n.</p>\r\n\r\n<p>Lợi &iacute;ch của hiệu thuốc chuyển đổi số kh&ocirc;ng chỉ d&agrave;nh cho c&aacute;c bệnh nh&acirc;n. Đối với c&aacute;c b&aacute;c sĩ l&acirc;m s&agrave;ng, việc chuyển quy tr&igrave;nh k&ecirc; đơn sang nền tảng số cấp cho họ c&aacute;i nh&igrave;n tổng quan hơn về bệnh nh&acirc;n, đồng thời gi&uacute;p c&aacute;c b&aacute;c sĩ tiết kiệm thời gian hơn trong việc k&ecirc; đơn v&agrave; gửi thuốc cho bệnh nh&acirc;n. Đối với dược sỹ, c&aacute;c quy tr&igrave;nh tự động sẽ gi&uacute;p họ dễ d&agrave;ng kiểm k&ecirc; hơn, cho ph&eacute;p c&aacute;c hiệu thuốc tiết kiệm thời gian v&agrave; nguồn lực.</p>\r\n\r\n<h2>Một tương lai to&agrave;n diện của ng&agrave;nh dược</h2>\r\n\r\n<p>Điều quan trọng cần lưu &yacute; l&agrave; việc&nbsp;<a href="https://viettimes.vn/tai-sao-cac-dich-vu-cong-nen-duoc-chuyen-sang-nen-tang-dam-may-bai-hoc-tu-anh-quoc-post161948.html">chuyển đổi số</a>&nbsp;trong lĩnh vực dược phẩm cần phải đi song song với c&aacute;c dịch vụ chăm s&oacute;c sức khỏe tuyến.</p>\r\n\r\n<p>Dịch vụ chăm s&oacute;c trực tiếp sẽ lu&ocirc;n đ&oacute;ng một vai tr&ograve; quan trọng trong việc hỗ trợ sức khỏe của cộng đồng v&agrave; sự hiện diện ở một mức độ đ&aacute;ng kể trong tương lai. Tuy nhi&ecirc;n, ch&uacute;ng ta kh&ocirc;ng thể để ng&agrave;nh dược bị bỏ lại ph&iacute;a sau khi phần c&ograve;n lại của hệ thống chăm s&oacute;c sức khỏe đang hiện đại h&oacute;a v&agrave; ph&aacute;t triển để đ&aacute;p ứng nhu cầu mới v&agrave; ph&aacute;t triển trong điều kiện thay đổi.</p>\r\n\r\n<p>Tương lai của ng&agrave;nh dược l&agrave; sự kết hợp giữa kỹ thuật số v&agrave; dịch vụ chăm s&oacute;c trực tiếp tốt nhất, kh&ocirc;ng nhất thiết phải chọn chỉ một trong hai. Một hệ thống chăm s&oacute;c sức khỏe hiện đại n&ecirc;n nắm lấy cả hai v&agrave; tận dụng những lợi &iacute;ch tương ứng m&agrave; ch&uacute;ng mang lại để cung cấp dịch vụ chăm s&oacute;c tốt nhất c&oacute; thể cho bệnh nh&acirc;n.</p>\r\n\r\n<p>Khi qu&aacute; tr&igrave;nh chuyển đổi số ng&agrave;y một ph&aacute;t triển, th&aacute;ch thức nằm ở việc đảm bảo ti&ecirc;u chuẩn cao, nhất qu&aacute;n trong c&aacute;c c&ocirc;ng nghệ được triển khai cũng như hướng dẫn bệnh nh&acirc;n v&agrave; nh&agrave; cung cấp về c&aacute;ch tiếp cận c&aacute;c lợi &iacute;ch mới c&oacute; sẵn. Bằng c&aacute;ch quy tr&aacute;ch nhiệm chung cho c&aacute;c nh&agrave; cung cấp c&ocirc;ng nghệ v&agrave; hiệu thuốc về việc cung cấp dịch vụ nhất qu&aacute;n cho tất cả c&aacute;c b&ecirc;n li&ecirc;n quan, ch&uacute;ng ta c&oacute; thể x&acirc;y dựng một tương lai tươi s&aacute;ng cho c&aacute;c hiệu thuốc v&agrave; c&aacute;c bệnh nh&acirc;n.</p>\r\n', '/chuyendoicongchung/4e9f959d2d9790c329cd750fd85e5247.png', '2022-12-01'),
	(34, 'Lễ Công bố số hóa và Kế hoạch chuyển đổi số ngành Dược', '<p>Ng&agrave;y 13/8/2020, tại Bộ Y tế đ&atilde; diễn ra Lễ C&ocirc;ng bố số h&oacute;a v&agrave; Kế hoạch chuyển đổi số ng&agrave;nh dược. Tham dự buổi Lễ, c&oacute; đồng ch&iacute; Đỗ Xu&acirc;n Tuy&ecirc;n, Thứ trưởng Bộ Y tế;&nbsp;đồng ch&iacute; Trương Quốc Cường, Thứ trưởng Bộ Y tế; đồng ch&iacute; Trần Văn Thuấn, Thứ trưởng Bộ Y tế; Thiếu tướng L&ecirc; Đăng Dũng, Quyền Chủ tịch ki&ecirc;m Tổng Gi&aacute;m đốc Viettel; L&atilde;nh đạo c&aacute;c Vụ, Cục, Văn ph&ograve;ng Bộ (Bộ Y tế)&hellip;</p>\r\n\r\n<p><img alt="" src="https://moh.gov.vn/documents/176127/0/13.8.2020.+so+hoa+anh+1.JPG/03e3b538-f476-4d9c-985e-990ff7541912?t=1597322132205" /></p>\r\n\r\n<p><em>C&aacute;c đại biểu thực hiện nghi thức chuyển đổi số h&oacute;a ng&agrave;nh Dược</em></p>\r\n\r\n<p>Ph&aacute;t biểu tại buổi Lễ, Thứ trưởng Bộ Y tế Trương Quốc Cường cho biết: Nhằm ph&aacute;t huy vai tr&ograve; của CNTT v&agrave; những cơ hội do kỷ nguy&ecirc;n số mang lại, Đảng, Ch&iacute;nh phủ đ&atilde; c&oacute; chủ trương, ch&iacute;nh s&aacute;ch s&aacute;ng suốt, đ&uacute;ng đắn, tạo điều kiện, th&uacute;c đẩy ph&aacute;t triển, ứng dụng CNTT, nhất l&agrave; việc x&acirc;y dựng Ch&iacute;nh phủ điện tử. Trong những năm qua, Bộ Y tế rất quan t&acirc;m chỉ đạo, d&agrave;nh nguồn lực cho việc đẩy mạnh x&acirc;y dựng, ứng dụng CNTT trong c&ocirc;ng t&aacute;c cải c&aacute;ch h&agrave;nh ch&iacute;nh cũng như hoạt động quản l&yacute; của Ng&agrave;nh với mục ti&ecirc;u phục vụ tốt hơn người d&acirc;n v&agrave; doanh nghiệp, g&oacute;p phần ho&agrave;n th&agrave;nh kế hoạch x&acirc;y dựng Ch&iacute;nh phủ điện tử. Hiện tại, Bộ Y tế l&agrave; một trong số những đơn vị đ&atilde; ho&agrave;n th&agrave;nh kết nối trực tuyến mức độ 4 đối với tất cả c&aacute;c TTHC thực hiện tại Bộ Y tế, một số đơn vị thuộc Bộ đ&atilde; ho&agrave;n th&agrave;nh việc số h&oacute;a v&agrave; đang triển khai x&acirc;y dựng kế hoạch nhằm thực hiện việc chuyển đổi số ng&agrave;nh Y tế.</p>\r\n\r\n<p><img alt="" src="https://moh.gov.vn/documents/176127/0/13.8.2020+TT+Cuong.jpg/3657b5bb-9182-40d9-bbee-9a832ca22950?t=1597322142602" /></p>\r\n\r\n<p><em>TS. Trương Quốc Cường, Thứ trưởng Bộ Y tế ph&aacute;t biểu tại buổi Lễ</em></p>\r\n\r\n<p>Việc ho&agrave;n th&agrave;nh số h&oacute;a ng&agrave;nh Dược l&agrave; nền tảng cơ bản để thực hiện chuyển đổi số trong ng&agrave;nh Dược, qua đ&oacute; g&oacute;p phần x&acirc;y dựng hệ thống y tế Việt Nam hiện đại, chất lượng, c&ocirc;ng bằng, hiệu quả v&agrave; hội nhập quốc tế; hỗ trợ người d&acirc;n, doanh nghiệp, c&aacute;n bộ y tế dễ d&agrave;ng tiếp cận th&ocirc;ng tin về thuốc; đồng thời gi&uacute;p tăng cường việc quản l&yacute;, đảm bảo sử dụng thuốc an to&agrave;n, hợp l&yacute; v&agrave; hiệu quả.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, Thứ trưởng cũng cho biết đ&acirc;y mới l&agrave; khởi đầu của giai đoạn mới - giai đoạn đ&ograve;i hỏi ch&uacute;ng ta phải n&acirc;ng cao tr&igrave;nh độ quản l&yacute; v&agrave; kỹ năng xử l&yacute; c&ocirc;ng việc. Chuyển đổi số trước ti&ecirc;n l&agrave; chuyển đổi nhận thức, c&aacute;c đơn vị cần tiến h&agrave;nh chuyển đổi số ngay th&ocirc;ng qua việc sử dụng nguồn lực, hệ thống kỹ thuật sẵn c&oacute; để số h&oacute;a; t&aacute;i cấu tr&uacute;c quy tr&igrave;nh nghiệp vụ, cơ cấu tổ chức v&agrave; chuyển đổi c&aacute;c mối quan hệ từ m&ocirc;i trường truyền thống sang m&ocirc;i trường số.</p>\r\n\r\n<p><img alt="" src="https://moh.gov.vn/documents/176127/0/13.8.2020+Toan+canh.jpg/5caf608c-b393-46dd-a2fb-094a4f12519c?t=1597322150621" /></p>\r\n\r\n<p>Ngay từ năm 2018, Cục Quản l&yacute; Dược &ndash; Bộ Y tế đ&atilde; thực hiện x&acirc;y dựng, kết nối, li&ecirc;n th&ocirc;ng th&agrave;nh c&ocirc;ng 13 dịch vụ c&ocirc;ng trực tuyến với Cơ chế một cửa quốc gia, song song với việc duy tr&igrave; c&aacute;c dịch vụ c&ocirc;ng trực tuyến trong lĩnh vực c&ocirc;ng bố mỹ phẩm v&agrave; x&aacute;c nhận th&ocirc;ng tin quảng c&aacute;o thuốc. Đến năm 2019, tiếp tục ho&agrave;n th&agrave;nh v&agrave; đưa v&agrave;o hoạt động c&aacute;c dịch vụ c&ocirc;ng trực tuyến phục vụ việc k&ecirc; khai/ k&ecirc; khai lại gi&aacute; thuốc; ho&agrave;n thiện từng bước c&aacute;c phần mềm xử l&yacute; t&aacute;c nghiệp đăng k&yacute; thuốc, quản l&yacute; chất lượng thuốc, quản l&yacute; kinh doanh dược để tiến tới cung cấp dịch vụ c&ocirc;ng trực tuyến trong lĩnh vực n&agrave;y.</p>\r\n\r\n<p>T&iacute;nh đến 30/6/2020, đ&atilde; ho&agrave;n th&agrave;nh x&acirc;y dựng dịch vụ c&ocirc;ng trực tuyến mức độ 4 kết nối Cổng dịch vụ c&ocirc;ng Bộ Y tế đối với to&agrave;n bộ 93 TTHC, đạt 100% v&agrave; đ&atilde; triển khai kết nối Cơ chế một cửa Quốc gia đối với 15 dịch vụ c&ocirc;ng trực tuyến. Từ ng&agrave;y 01/01/2020 đến nay, số hồ sơ được xử l&yacute; trực tuyến l&agrave; 18.027 hồ sơ.</p>\r\n\r\n<p>X&acirc;y dựng Hệ thống Cơ sở dữ liệu Dược Quốc gia, kết nối, li&ecirc;n th&ocirc;ng c&aacute;c cơ sở cung ứng thuốc theo Chỉ thị số 23/CT-TTg ng&agrave;y 23/8/2018 của Thủ tướng Ch&iacute;nh phủ: Hiện tại, đ&atilde; ho&agrave;n th&agrave;nh kết nối 63/63 tỉnh/th&agrave;nh phố, gần 100% tr&ecirc;n tổng số 60.724 cơ sở cung ứng thuốc đ&atilde; c&oacute; phần mềm, 60% đ&atilde; li&ecirc;n th&ocirc;ng dữ liệu, quản l&yacute; hơn 7,2 triệu đơn thuốc, 26,7 triệu h&oacute;a đơn b&aacute;n h&agrave;ng, gần 4 triệu phiếu xuất nhập kho. Th&aacute;ng 8/2019, Ng&acirc;n h&agrave;ng dữ liệu ng&agrave;nh Dược (drugbank.vn) đ&atilde; được khai trương với th&ocirc;ng tin tra cứu của hơn 10.000 thuốc. Đến nay đ&atilde; cung cấp dữ liệu của 15.226 loại thuốc đang được cấp ph&eacute;p lưu h&agrave;nh c&ograve;n hiệu lực tại Việt Nam. Đồng thời chuẩn h&oacute;a v&agrave; x&acirc;y dựng Phần mềm m&atilde; định danh cho hơn 23.500 thuốc được cấp ph&eacute;p lưu h&agrave;nh c&ograve;n hiệu lực tại Việt Nam.</p>\r\n\r\n<p>Để khắc phục t&igrave;nh trạng cấp tr&ugrave;ng chứng chỉ h&agrave;nh nghề ở c&aacute;c địa phương cũng như gi&uacute;p c&aacute;c cơ quan qu&acirc;n l&yacute; gi&aacute;m s&aacute;t việc cấp chứng chỉ h&agrave;nh nghề dược, Cục Quản l&yacute; Dược đ&atilde; x&acirc;y dựng phần mềm quản l&yacute; dữ liệu h&agrave;nh nghề v&agrave; kinh doanh dược với th&ocirc;ng tin về to&agrave;n bộ c&aacute;c cơ sở kinh doanh dược (340 cơ sở sản xuất, xuất nhập khẩu, 4.079 cơ sở b&aacute;n bu&ocirc;n, 61.000 cơ sở b&aacute;n lẻ thuốc) v&agrave; 115.080 chứng chỉ h&agrave;nh nghề đ&atilde; được cấp tr&ecirc;n to&agrave;n quốc.</p>\r\n\r\n<p>Việc ho&agrave;n th&agrave;nh số h&oacute;a ng&agrave;nh Dược l&agrave; nền tảng cơ bản để thực hiện chuyển đổi số trong ng&agrave;nh Dược, qua đ&oacute; g&oacute;p phần x&acirc;y dựng hệ thống y tế Việt Nam hiện đại, chất lượng, c&ocirc;ng bằng, hiệu quả v&agrave; hội nhập quốc tế; hỗ trợ người d&acirc;n, doanh nghiệp, c&aacute;n bộ y tế dễ d&agrave;ng tiếp cận th&ocirc;ng tin về thuốc; đồng thời gi&uacute;p tăng cường việc quản l&yacute;, đảm bảo sử dụng thuốc an to&agrave;n, hợp l&yacute; v&agrave; hiệu quả.</p>\r\n\r\n<p>Từ đầu năm 2020, trước t&igrave;nh h&igrave;nh diễn biến hết sức phức tạp cua dịch COVID-19 tr&ecirc;n to&agrave;n thể giới, Cục Quản l&yacute; Dược đ&atilde; chủ động phối hợp với c&aacute;c đơn vị li&ecirc;n quan x&acirc;y dụng phần mềm để c&aacute;c cơ sở sản xuất thuốc, nhập khẩu thuốc b&aacute;o c&aacute;o li&ecirc;n tục việc xuất, nhập, tồn cũng như kế hoạch sản xuất, nhập khẩu c&aacute;c thuốc được khuyến nghị sử dụng trong hướng dẫn chẩn đo&aacute;n v&agrave; điều trị COVID-19 với 48 hoạt chất. Trong thời gian tới, Cục Quản l&yacute; Dược sẽ tiếp tục ph&aacute;t triển phần mềm kết nối tới tất cả c&aacute;c nh&agrave; m&aacute;y để quản l&yacute; v&agrave; c&oacute; số liệu b&aacute;o c&aacute;o, thống k&ecirc; của khoảng 700 hoạt chất d&ugrave;ng trong sản xuất thuốc tại Việt Nam.</p>\r\n\r\n<p>Bắt đầu từ th&aacute;ng 01/2020, cho đến nay. Cục Quản l&yacute; Dược đ&atilde; ho&agrave;n th&agrave;nh việc x&acirc;y dựng hệ thống phần mềm khởi tạo m&atilde; định danh cho từmg loại thuốc. Tới thời điểm n&agrave;y to&agrave;n bộ m&atilde; định danh đ&atilde; ho&agrave;n thiện v&agrave; trở th&agrave;nh khối dữ liệu cơ bản, n&ecirc;n t&agrave;ng của Cục Quản l&yacute; Dược cũng như l&agrave; khối dữ liệu d&ugrave;ng chung cho to&agrave;n ng&agrave;nh Y tế trong qu&aacute; tr&igrave;nh chuyển đổi số. Hiện c&oacute; 23.500 thuốc được cấp ph&eacute;p lưu h&agrave;nh c&ograve;n hiệu lực v&agrave; trong số đ&oacute; 100% l&agrave; thuốc k&ecirc; đơn đ&atilde; được cấp m&atilde; định danh&hellip;.</p>\r\n\r\n<p>Đ&acirc;y kh&ocirc;ng chỉ l&agrave; dữ liệu của Cục quản l&yacute; Dược m&agrave; c&ograve;n l&agrave; dữ liệu d&ugrave;ng chung của to&agrave;n ng&agrave;nh Y tế trong tất cả c&aacute;c hệ thống: Đơn thuốc điện tử, Bệnh &aacute;n điện tử, Y bạ điện tử, Giảm định thanh to&aacute;n bảo hiểm y tế cũng như c&aacute;c phần mềm kh&aacute;c đ&atilde; v&agrave; đang sử dụng trong ng&agrave;nh Y tế.</p>\r\n\r\n<p>Ph&aacute;t biểu tại buổi Lễ, Thiếu tướng L&ecirc; Đăng Dũng, Quyền Chủ tịch ki&ecirc;m Tổng Gi&aacute;m đốc Viettel cho biết, Viettel lu&ocirc;n sẵn s&agrave;ng về c&ocirc;ng nghệ để đ&aacute;p ứng nhu cầu, tuy nhi&ecirc;n việc &aacute;p dụng th&agrave;nh c&ocirc;ng hay kh&ocirc;ng th&igrave; kh&ocirc;ng chỉ c&oacute; nguy&ecirc;n nh&acirc;n do c&ocirc;ng nghệ m&agrave; phần lớn quyết định th&agrave;nh c&ocirc;ng l&agrave; do nhận thức v&agrave; sự quyết t&acirc;m ch&iacute;nh trị của đơn vị, của người đứng đầu đơn vị thực hiện chuyển đổi số./</p>\r\n', '/chuyendoicongchung/635d1c7b981d21a5cd96fdd903cf5812.jpg', '2022-12-01'),
	(35, 'Hội nghị chuyển đổi số ngành Y tế năm 2022 khu vực phía Nam', '<p><strong>Ng&agrave;y 28/11/2022, Bộ Y tế tổ chức hội nghị Chuyển đổi số ng&agrave;nh Y tế năm 2022 khu vực ph&iacute;a Nam. GS.TS.Trần Văn Thuấn, Thứ trưởng Bộ Y tế dự v&agrave; ph&aacute;t biểu tại hội nghị.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Tham dự c&oacute; đại diện một số Vụ, Cục, Văn ph&ograve;ng Bộ Y tế; l&atilde;nh đạo UBND tỉnh B&agrave; Rịa &ndash; Vũng T&agrave;u; UBND tỉnh Đồng Nai, Sở Y tế v&agrave; một số đơn vị li&ecirc;n quan.</h2>\r\n\r\n<h2><img alt="" src="https://moh.gov.vn/documents/20182/1410811/28.11.2022.+HNCDS+1.jpg/5eb6e63c-003c-4732-b93a-b1287de3ba73?t=1669695707329" /></h2>\r\n\r\n<h2><em>GS.TS.Trần Văn Thuấn, Thứ trưởng Bộ Y tế ph&aacute;t biểu</em></h2>\r\n\r\n<h2>Ph&aacute;t biểu khai mạc hội nghị, GS.TS.Trần Văn Thuấn, Thứ trưởng Bộ Y tế cho biết: hơn hai năm qua, dưới sự chỉ đạo của Ban Chỉ đạo Quốc gia ph&ograve;ng, chống dịch COVID-19, Bộ Y tế đ&atilde; nỗ lực phối hợp với c&aacute;c Bộ, ng&agrave;nh, ch&iacute;nh quyền địa phương triển khai quyết liệt c&aacute;c giải ph&aacute;p kiểm so&aacute;t tốt dịch bệnh, đồng thời tạo điều kiện thực hiện hoạt động ph&aacute;t triển hoạt động kinh tế - x&atilde; hội.</h2>\r\n\r\n<h2>&ldquo;Kết quả n&agrave;y c&oacute; được l&agrave; nhờ sự kết hợp h&agrave;i h&ograve;a của nhiều yếu tố, trong đ&oacute; kh&ocirc;ng thể kh&ocirc;ng n&oacute;i đến sự đ&oacute;ng g&oacute;p kh&ocirc;ng nhỏ của việc ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin v&agrave; chuyển đổi số y tế&rdquo; - Thứ trưởng n&oacute;i.</h2>\r\n\r\n<h2>Thứ trưởng Trần Văn Thuấn cho biết th&ecirc;m: Bộ Y tế, Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng, Bộ C&ocirc;ng an v&agrave; c&aacute;c doanh nghiệp c&ocirc;ng nghệ th&ocirc;ng tin đ&atilde; phối hợp triển khai rất hiệu quả nhiều ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin phục vụ cho c&ocirc;ng t&aacute;c ph&ograve;ng, &nbsp;chống dịch bệnh COVID-19 như: C&aacute;c ứng dụng quản l&yacute; khai b&aacute;o y tế, x&eacute;t nghiệm, quản l&yacute; ca bệnh, quản l&yacute; điều trị bệnh nh&acirc;n COVID-19, quản l&yacute; oxy y tế&hellip;; Triển khai nền tảng Quản l&yacute; ti&ecirc;m chủng COVID-19 phục vụ cho chiến dịch ti&ecirc;m chủng lớn nhất trong lịch sử; Kh&aacute;nh th&agrave;nh v&agrave; đưa v&agrave;o sử dụng hệ thống kh&aacute;m chữa bệnh từ xa tại hơn 1.000 cơ sở y tế trong cả nước; C&aacute;c ứng dụng tư vấn kh&aacute;m chữa bệnh từ xa, hỗ trợ người d&acirc;n&hellip;</h2>\r\n\r\n<h2>Thực hiện Nghị quyết số 52-NQ/TW ng&agrave;y 27/09/2019 của Bộ Ch&iacute;nh trị về một số chủ trương, ch&iacute;nh s&aacute;ch chủ động tham gia cuộc c&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư v&agrave; Quyết định số 749/QĐ-TTg ng&agrave;y 03/6/2020 của Thủ tướng Ch&iacute;nh phủ ph&ecirc; duyệt &ldquo;Chương tr&igrave;nh Chuyển đổi số quốc gia đến năm 2025, định hướng đến năm 2030&rdquo;, ng&agrave;y 22/12/2020 Bộ Y tế đã ban h&agrave;nh Quyết định số 5316/QĐ-BYT ph&ecirc; duyệt chương tr&igrave;nh chuyển đổi số y tế đến năm 2025, định hướng đến năm 2030.</h2>\r\n\r\n<h2>&nbsp;&ldquo;Thời gian vừa qua c&aacute;c đơn vị, địa phương đ&atilde; nỗ lực, ưu ti&ecirc;n nhiều nguồn lực triển khai chuyển đổi số y tế, đến nay việc chuyển đổi số y tế đ&atilde; đạt được c&aacute;c kết quả t&iacute;ch cực về chuyển đổi nhận thức; về kiến tạo thể chế; về ph&aacute;t triển hạ tầng số; về ph&aacute;t triển c&aacute;c nền tảng số v&agrave; về an to&agrave;n, an ninh mạng. Thay mặt l&atilde;nh đạo Bộ Y tế, t&ocirc;i ghi nhận, biểu dương v&agrave; đ&aacute;nh gi&aacute; cao những nỗ lực, quyết t&acirc;m v&agrave; những kết quả đạt được của c&aacute;c đơn vị, địa phương trong c&ocirc;ng cuộc chuyển đổi số y tế, g&oacute;p phần quan trọng v&agrave;o c&ocirc;ng cuộc chuyển đổi số quốc gia của ch&uacute;ng ta&rdquo; - Thứ trưởng đ&aacute;nh gi&aacute;.</h2>\r\n\r\n<h2><img alt="" src="https://moh.gov.vn/documents/20182/1410811/28.11.2022.+HNCDS+3.jpg/72ff020c-1604-44d1-ab3e-210234bf307d?t=1669695716568" /></h2>\r\n\r\n<h2><em>C&aacute;c đại biểu tham dự hội nghị</em></h2>\r\n\r\n<h2>Tuy nhi&ecirc;n, Thứ trưởng Bộ Y tế Trần Văn Thuấn cũng chỉ r&otilde; một số tồn tại, kh&oacute; khăn, vướng mắc cần được giải quyết một c&aacute;ch triệt để như: nhiều địa phương, đơn vị chưa thực sự quan t&acirc;m đến việc đẩy mạnh ứng dụng CNTT v&agrave; chuyển đổi số y tế; Nguồn nh&acirc;n lực v&agrave; hạ tầng CNTT tại c&aacute;c đơn vị c&ograve;n thiếu v&agrave; yếu do thiếu cơ chế t&agrave;i ch&iacute;nh, nguồn lực đầu tư cho chuyển đổi số y tế; Số lượng c&aacute;c cơ sở kh&aacute;m chữa bệnh triển khai bệnh &aacute;n điện tử theo Th&ocirc;ng tư số 46/2018/TT-BYT c&ograve;n rất hạn chế, mới c&oacute; 37 cơ sở tr&ecirc;n to&agrave;n quốc, con số n&agrave;y rất khi&ecirc;m tốn so với mục ti&ecirc;u v&agrave; lộ tr&igrave;nh đặt ra, b&ecirc;n cạnh đ&oacute; Th&ocirc;ng tư 46/2018/TT-BYT hiện c&ograve;n những quy định kh&ocirc;ng c&ograve;n ph&ugrave; hợp với thực tiễn; Số lượng cơ sở kh&aacute;m, chữa bệnh triển khai y tế từ xa đang ng&agrave;y c&agrave;ng phổ biến, tuy nhi&ecirc;n hiệu quả triển khai c&ograve;n chưa r&otilde; r&agrave;ng khi vướng mắc về cơ chế t&agrave;i ch&iacute;nh v&agrave; c&aacute;c quy định c&oacute; li&ecirc;n quan của ph&aacute;p luật về kh&aacute;m chữa bệnh.</h2>\r\n\r\n<h2>Tại hội nghị, Thứ trưởng Trần Văn Thuấn đề nghị c&aacute;c đơn vị chuy&ecirc;n m&ocirc;n thuộc Bộ Y tế, c&aacute;c địa phương, đơn vị thực hiện một số nội dung sau:</h2>\r\n\r\n<h2><em>Thứ nhất:</em>&nbsp;tập trung ho&agrave;n thiện thể chế, ch&iacute;nh s&aacute;ch, trong đ&oacute; c&oacute; nội dung x&acirc;y dựng v&agrave; tr&igrave;nh Ch&iacute;nh phủ dự thảo Nghị định quy định quản l&yacute; dữ liệu y tế; ban h&agrave;nh Th&ocirc;ng tư điều chỉnh, bổ sung của c&aacute;c Th&ocirc;ng tư số 49/2017/TT-BYT, Th&ocirc;ng tư số 54/2017/TT-BYT v&agrave; Th&ocirc;ng tư số 46/2018/TT-BYT. Triển khai c&aacute;c giải ph&aacute;p nhằm n&acirc;ng cao xếp hạng chỉ số đ&aacute;nh gi&aacute; chuyển đổi số (DTI) của Bộ Y tế, ưu ti&ecirc;n th&uacute;c đẩy những chỉ số c&ograve;n thấp.</h2>\r\n\r\n<h2><em>Thứ hai:</em>&nbsp;tập trung triển khai Đề &aacute;n Ph&aacute;t triển ứng dụng dữ liệu d&acirc;n cư, định danh v&agrave; x&aacute;c thực điện tử phục vụ chuyển đổi số quốc gia giai đoạn 2022-2025, tầm nh&igrave;n đến năm 2030 (Đề &aacute;n 06) mang lại lợi &iacute;ch thiết thực cho người d&acirc;n trong c&ocirc;ng t&aacute;c chăm s&oacute;c sức khỏe.</h2>\r\n\r\n<h2><em>Thứ ba:</em>&nbsp;C&aacute;c đơn vị, địa phương khẩn trương x&acirc;y dựng kế hoạch chuyển đổi số tổng thể, to&agrave;n diện, b&aacute;m s&aacute;t c&aacute;c văn bản hướng dẫn, c&aacute;c chương tr&igrave;nh, đề &aacute;n của Bộ Y tế; khẩn trương ban h&agrave;nh Kế hoạch triển khai Quyết định số 2955/QĐ-BYT của Bộ Y tế ph&ecirc; duyệt Kế hoạch th&uacute;c đẩy ph&aacute;t triển v&agrave; sử dụng c&aacute;c nền tảng số y tế thực hiện chương tr&igrave;nh chuyển đổi số quốc gia đến năm 2025, định hướng đến năm 2030.</h2>\r\n\r\n<h2><em>Thứ tư:</em>&nbsp;Tăng cường truyền th&ocirc;ng về lợi &iacute;ch của chuyển đổi số y tế để c&aacute;c cơ quan, đơn vị, người d&acirc;n, doanh nghiệp hiểu r&otilde; về lợi &iacute;ch v&agrave; hiệu quả của chuyển đổi số y tế. Th&uacute;c đẩy thanh to&aacute;n kh&ocirc;ng d&ugrave;ng tiền mặt, sử dụng thẻ CCCD gắn ch&iacute;p khi kh&aacute;m chữa bệnh BHYT tại c&aacute;c cơ sở y tế.</h2>\r\n\r\n<h2><img alt="" src="https://moh.gov.vn/documents/20182/1410811/28.11.2022.+HNCDS+2.jpg/24e25f0e-46e0-46b1-b105-b75a70acd25e?t=1669695712326" /></h2>\r\n\r\n<h2><em>&Ocirc;ng Đỗ Trường Duy, Cục trưởng Cục C&ocirc;ng nghệ th&ocirc;ng tin ph&aacute;t biểu tại hội nghị</em></h2>\r\n\r\n<h2>Theo &ocirc;ng Đỗ Trường Duy, Cục trưởng Cục C&ocirc;ng nghệ th&ocirc;ng tin (Trung t&acirc;m Th&ocirc;ng tin y tế quốc gia) cho biết, ngay sau khi Quyết định số 749/QĐ-TTg ng&agrave;y 03/6/2020 của Thủ tướng Ch&iacute;nh phủ ph&ecirc; duyệt &ldquo;Chương tr&igrave;nh chuyển đổi số quốc gia đến năm 2025, định hướng đến năm 2030&rdquo;. Ng&agrave;y 22/12/2020 Bộ Y tế ban h&agrave;nh Quyết định 5316/QĐ-BYT ph&ecirc; duyệt Chương tr&igrave;nh chuyển đổi số y tế đến năm 2025, định hướng đến năm 2030 v&agrave; Quyết định số 2955/QĐ-BYT ng&agrave;y 28/10/2022 ph&ecirc; duyệt Kế hoạch th&uacute;c đẩy ph&aacute;t triển v&agrave; sử dụng c&aacute;c nền tảng số y tế thực hiện chương tr&igrave;nh chuyển đổi số quốc gia đến năm 2025, định hướng đến năm 2030 đ&atilde; cụ thể h&oacute;a một số mục ti&ecirc;u, hoạt động chuyển đổi số y tế. Đ&acirc;y l&agrave; cơ sở để địa phương, đơn vị x&aacute;c định mục ti&ecirc;u, kết quả kế hoạch chuyển đổi số y tế của địa phương đơn vị m&igrave;nh&hellip;</h2>\r\n\r\n<h2>Quyết định số 06/QĐ-TTg ph&ecirc; duyệt Đề &aacute;n &ldquo;Ph&aacute;t triển ứng dụng dữ liệu về d&acirc;n cư, định danh v&agrave; x&aacute;c thực điện tử phục vụ chuyển đổi số quốc gia giai đoạn 2022 - 2025, tầm nh&igrave;n đến năm 2030&rdquo; (gọi tắt l&agrave; Đề &aacute;n 06) n&ecirc;u r&otilde; nội dung trọng t&acirc;m l&agrave; đảm bảo cơ sở dữ liệu quốc gia về d&acirc;n cư &ldquo;đ&uacute;ng, đủ, sạch, sống&rdquo; th&ocirc;ng qua to&agrave;n bộ qu&aacute; tr&igrave;nh khởi tạo, l&agrave;m sạch, cập nhật, khai th&aacute;c tiện &iacute;ch đối với dữ liệu của c&ocirc;ng d&acirc;n trong c&aacute;c lĩnh vực ưu ti&ecirc;n chuyển đổi số quốc gia, trong đ&oacute; c&oacute; lĩnh vực y tế.</h2>\r\n\r\n<h2>&Ocirc;ng Đỗ Trường Duy cho biết th&ecirc;m, Bộ Y tế được giao c&aacute;c nhiệm vụ thực hiện Đề &aacute;n 06 c&oacute; t&iacute;nh trọng t&acirc;m theo giai đoạn:</h2>\r\n\r\n<h2>- Nh&oacute;m 1: Giai đoạn 06 th&aacute;ng đầu năm 2022, thời điểm ban h&agrave;nh Quyết định 06 của Thủ tướng Ch&iacute;nh phủ cũng l&agrave; thời điểm dịch COVID-19 c&ograve;n diễn biến phức tạp, trọng t&acirc;m nhiệm vụ của Bộ Y tế l&agrave; l&agrave;m &ldquo;đ&uacute;ng, đủ, sạch, sống&rdquo; dữ liệu c&ocirc;ng d&acirc;n phục vụ ph&ograve;ng, chống dịch COVID-19. Dữ liệu li&ecirc;n quan bao gồm dữ liệu ti&ecirc;m chủng COVID-19, dữ liệu x&eacute;t nghiệm COVID -19, dữ liệu F0 khỏi bệnh.</h2>\r\n\r\n<h2>- Nh&oacute;m 2: Giai đoạn 06 th&aacute;ng cuối năm 2022, t&igrave;nh h&igrave;nh dịch COVID -19 kh&ocirc;ng c&ograve;n phức tạp, nhiệm vụ được giao của Bộ Y tế chuyển sang trọng t&acirc;m l&agrave; dữ liệu về th&ocirc;ng tin sức khỏe c&aacute; nh&acirc;n, đảm bảo dữ liệu &ldquo;đ&uacute;ng, đủ, sạch, sống&rdquo; trong c&ocirc;ng t&aacute;c kh&aacute;m, chữa bệnh.</h2>\r\n\r\n<h2>- Nh&oacute;m 3: Trong thời gian tới, c&aacute;c nhiệm vụ được giao của Bộ Y tế sẽ chuyển trọng t&acirc;m để đ&aacute;p ứng kết nối th&ocirc;ng tin, dữ liệu nh&agrave; thuốc, cơ sở kh&aacute;m bệnh, chữa bệnh, quản l&yacute; chứng chỉ h&agrave;nh nghề dược, h&agrave;nh nghề kh&aacute;m bệnh, chữa bệnh với cơ sở dữ liệu quốc gia về d&acirc;n cư, cũng như kết nối th&ocirc;ng tin thực hiện thủ tục h&agrave;nh ch&iacute;nh (dịch vụ c&ocirc;ng trực tuyến) của c&aacute;c doanh nghiệp trong lĩnh vực, dược, an to&agrave;n thực phẩm, trang thiết bị v&agrave; vật tư y tế.</h2>\r\n\r\n<h2>&ldquo;Ch&uacute;ng ta cần thống nhất chung nhận thức về tầm quan trọng của Đề &aacute;n 06 v&agrave; mối quan hệ giữa Đề &aacute;n v&agrave; chương tr&igrave;nh chuyển đổi số quốc gia v&agrave; c&aacute;c hoạt động chuyển đổi số y tế. Từ đ&oacute; thống nhất về việc x&aacute;c định mục ti&ecirc;u chung, mục ti&ecirc;u cụ thể trước mắt v&agrave; c&aacute;ch thức tổ chức thực hiện để triển khai th&agrave;nh c&ocirc;ng v&agrave; hiệu quả c&aacute;c nhiệm vụ chuyển đổi số y tế, phục vụ sự nghiệp chăm s&oacute;c v&agrave; bảo vệ sức khỏe nh&acirc;n d&acirc;n&rdquo;- &Ocirc;ng Đỗ Trường Duy n&oacute;i.</h2>\r\n\r\n<h2><img alt="" src="https://moh.gov.vn/documents/20182/1410811/28.11.2022.+HNCDS+4.jpg/8d36dcda-4213-46cc-b94f-6bf66c36c80b?t=1669695721017" /></h2>\r\n\r\n<h2><em>TS.BS. H&agrave; Anh Đức, Ch&aacute;nh Văn ph&ograve;ng Bộ Y tế tham luận t&igrave;nh h&igrave;nh triển khai ứng dụng CNTT trong giải quyết thủ tục h&agrave;nh ch&iacute;nh tại Bộ Y tế</em></h2>\r\n\r\n<h2>Tại hội nghị c&aacute;c đại biểu cũng được nghe một số tham luận như: Phổ biến triển khai Kế hoạch th&uacute;c đẩy c&aacute;c nền tảng số y tế v&agrave; hướng dẫn thực hiện; Giới thiệu dự thảo sửa đổi Th&ocirc;ng tư 53/2014/TT-BYT, 49/2017/TT-BYT, 54/2017/TT-BYT, 46/2018/TT-BYT; B&aacute;o c&aacute;o t&igrave;nh h&igrave;nh triển khai ứng dụng CNTT trong giải quyết thủ tục h&agrave;nh ch&iacute;nh tại Bộ Y tế; Ứng dụng CNTT tại Bệnh viện Đại học Y Dược tp. HCM; B&agrave;i học kinh nghiệm về ứng dụng CNTT trong kh&aacute;m bệnh chữa bệnh v&agrave; chuyển đổi số y tế tại Nhật Bản; C&aacute;c giải ph&aacute;p đảm bảo an to&agrave;n trong chuyển đổi số; Kết quả thực hiện chuyển đổi số v&agrave; kh&oacute; khăn, th&aacute;ch thức của ng&agrave;nh y tế tại Vĩnh Long; Hệ thống th&ocirc;ng tin quản l&yacute; ng&agrave;nh Y tế tại SYT Đồng Th&aacute;p; Giới thiệu về dịch vụ Google Cloud d&agrave;nh cho Y tế, c&aacute;c c&ocirc;ng nghệ API hỗ trợ Hồ sơ sức khoẻ điện tử (EHR) v&agrave; Hồ sơ sức khoẻ c&aacute; nh&acirc;n (PHR); Y tế số cho người d&acirc;n: Ứng dụng dữ liệu 4210 &ndash; Hồ sơ sức khoẻ c&aacute; nh&acirc;n &ndash; hồ sơ sức khoẻ điện tử trong chăm s&oacute;c người bệnh; X&acirc;y dựng kho dữ liệu y tế v&agrave; ứng dụng tr&iacute; tuệ nh&acirc;n tạo trong khai th&aacute;c dữ liệu&hellip;/.</h2>\r\n', '/chuyendoicongchung/e329bb54ba87f1a752f553f759f6ef1b.jpg', '2022-12-01');
