INSERT INTO `diemtin` (`ID_DT`, `ID_CHUYENMUC_DT`, `ID_ADMIN`, `TIEUDE_DT`, `NOIDUNG_DT`, `HINHANH_DT`, `NGAYDANG_DT`, `created_at`, `updated_at`) VALUES
	(18, 1, 0, 'Tác hại của thuốc lá điện tử và thuốc lá nung nóng đối với sức khỏe', '<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">C&aacute;c sản phẩm thuốc l&aacute; mới hiện được c&aacute;c c&ocirc;ng ty thuốc l&aacute; giới thiệu l&agrave; một sản phẩm &iacute;t hại hơn so với thuốc l&aacute; điếu th&ocirc;ng thường. Tuy nhi&ecirc;n, đ&acirc;y chỉ l&agrave; c&aacute;c quảng c&aacute;o nhằm g&acirc;y nhầm lẫn. C&aacute;c bằng chứng đến thời điểm hiện tại cho thấy cả thuốc l&aacute; điện tử ENDs v&agrave; thuốc l&aacute; nung n&oacute;ng HTPs đều c&oacute; chứa c&aacute;c th&agrave;nh phần độc hại, khi sử dụng đều c&oacute; sự đốt ch&aacute;y v&agrave; c&oacute; kh&oacute;i, hơi tỏa ra m&ocirc;i trường g&acirc;y hại cho người xung quanh.</span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff"><strong>T&aacute;c hại của thuốc l&aacute; điện tử</strong></span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">- Thuốc l&aacute; điện tử chứa nhiều chất độc hại, g&acirc;y nghiện, g&acirc;y bệnh tật, tử vong. Theo Li&ecirc;n minh Kiểm so&aacute;t thuốc l&aacute; Đ&ocirc;ng Nam &Aacute; (SEATCA) đa phần thuốc l&aacute; điện tử c&oacute; chứa Nicotine - chất g&acirc;y nghiện cao, l&agrave; nguy&ecirc;n nh&acirc;n g&acirc;y c&aacute;c bệnh tim mạch, h&ocirc; hấp, ti&ecirc;u h&oacute;a, v&agrave; ung thư. Sử dụng nicotine qu&aacute; liều g&acirc;y ngộ độc. Nghiện nicotine l&agrave; một trong những nguy&ecirc;n nh&acirc;n g&acirc;y c&aacute;c bệnh tim mạch, đột quỵ</span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">Đối với trẻ em v&agrave; phụ nữ mang thai: Nicotine trong thuốc l&aacute; điện tử g&acirc;y hại cho sự ph&aacute;t triển n&atilde;o bộ ở trẻ em v&igrave; n&atilde;o bộ của trẻ đang trong giai đoạn ph&aacute;t triển. Nicotine cũng ảnh hưởng xấu tới sức khỏe của b&agrave; mẹ v&agrave; thai nhi trong thai k&igrave;, g&acirc;y ra sinh non v&agrave; thai chết lưu.</span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">Đối với thanh thiếu ni&ecirc;n: Những thay đổi do nicotine g&acirc;y ra trong hệ thần kinh trong n&atilde;o khiến người d&ugrave;ng ở nh&oacute;m tuổi trẻ dễ bị nghiện nicotine hơn v&agrave; v&igrave; thế ảnh hưởng đến sức khỏe sẽ đến sớm v&agrave; trầm trọng hơn trong tương lai.</span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">- Trong dung dịch thuốc l&aacute; điện tử c&ograve;n c&oacute;: glycerin, propylene glycol v&agrave; hương liệu (c&oacute; tr&ecirc;n 15,500 c&aacute;c loại hương liệu). Propylene glycol (mặc d&ugrave; được coi l&agrave; an to&agrave;n trong thực phẩm nhưng kh&ocirc;ng phải để h&iacute;t) c&oacute; thể tạo th&agrave;nh propylene oxide, một chất g&acirc;y ung thư khi được đun n&oacute;ng v&agrave; h&oacute;a hơi. Glycerin/Glycerin gốc thực vật: khi được đun n&oacute;ng v&agrave; h&oacute;a hơi, tạo th&agrave;nh acrolein, g&acirc;y k&iacute;ch ứng đường h&ocirc; hấp tr&ecirc;n.</span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">C&aacute;c chất độc hại trong thuốc l&aacute; điện tử c&ograve;n li&ecirc;n quan đến nhồi m&aacute;u cơ tim, g&acirc;y tổn thương phổi. Thời gian gần đ&acirc;y, số liệu từ c&aacute;c quốc gia cho thấy sử dụng thuốc l&aacute; điện tử g&acirc;y c&aacute;c ca vi&ecirc;m phổi cấp nguy kịch (39 ca tử vong); động kinh, c&aacute;c bệnh về răng miệng, ngộ độc (&gt;2.600 ca ở Mỹ, EU, Canada, Nam Triều Ti&ecirc;n); g&acirc;y tai nạn thương t&iacute;ch (c&aacute;c vụ nổ/bỏng, chấn thương, g&atilde;y xương chiếm tới 2.035 ca ở Mỹ giai đoạn 2015-2017, trong đ&oacute; c&oacute; 2 ca tử vong);...</span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">- Thuốc l&aacute; điện tử tiện dụng với c&aacute;c ống dung dịch được đ&oacute;ng g&oacute;i cho h&agrave;ng trăm hơi h&uacute;t, hầu như kh&ocirc;ng c&oacute; định lượng về nồng độ nicotine trong mỗi ml. Điều n&agrave;y dẫn đến nguy cơ người sử dụng tăng liều lượng, mức độ dung nạp nicotine v&agrave; g&acirc;y nguy cơ ngộ độc cấp t&iacute;nh.</span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">- Thuốc l&aacute; điện tử sử dụng nhiều hương liệu, h&oacute;a chất kh&ocirc;ng phải l&agrave; từ nguy&ecirc;n liệu l&aacute; thuốc l&aacute; điếu th&ocirc;ng thường. Nguy&ecirc;n liệu phối trộn nhiều loại th&agrave;nh phần kh&aacute;c nhau n&ecirc;n c&oacute; thể bị lợi dụng để sử dụng ma t&uacute;y th&ocirc;ng qua việc phối trộn. Tại Việt Nam, t&igrave;nh trạng tội phạm ma t&uacute;y trộn c&aacute;c loại ma t&uacute;y v&agrave;o c&aacute;c sản phẩm thuốc l&aacute; điện tử v&agrave; hướng v&agrave;o đối tượng học sinh, sinh vi&ecirc;n v&agrave; giới trẻ đang rất phức tạp v&agrave; một số vụ việc đ&atilde; được điều tra, ph&aacute;t hiện.</span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff"><strong>T&aacute;c hại của thuốc l&aacute; l&agrave;m n&oacute;ng:</strong></span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">Tổ chức Y tế thế giới khuyến c&aacute;o: Thuốc l&aacute; l&agrave;m n&oacute;ng tạo ra chất kh&iacute; độc hại, c&oacute; nhiều chất độc giống như trong kh&oacute;i thuốc l&aacute; điếu th&ocirc;ng thường. D&ugrave; HTPs được nung ở nhiệt độ thấp hơn, nhưng vẫn tạo ra những h&oacute;a chất tương tự như trong kh&oacute;i thuốc l&aacute; (Acrolein, Volatile Organic Chemicals, Acetaldehyde, Carbon monoxide, Polycyclic Aromatic Hydrocarbons, Heavy metals, Formaldehyde... - một số h&oacute;a chất được xếp v&agrave;o nh&oacute;m g&acirc;y ung thư). Nồng độ một số h&oacute;a chất thấp hơn trong thuốc l&aacute; điếu th&ocirc;ng thường, nhưng nồng độ một số h&oacute;a chất kh&aacute;c lại cao hơn, v&agrave; nồng độ h&oacute;a chấp thấp kh&ocirc;ng đồng nghĩa với giảm nguy cơ sức khỏe.</span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">Kh&oacute;i thuốc nung ngo&agrave;i g&acirc;y hại cho người h&uacute;t th&igrave; c&ograve;n g&acirc;y t&aacute;c hại cho người xung quanh do h&uacute;t thuốc thụ động. Thuốc l&aacute; l&agrave;m n&oacute;ng chứa nicotine l&agrave; chất g&acirc;y nghiện mạnh, c&oacute; hại cho sức khỏe, đặc biệt đối với trẻ em, vị th&agrave;nh ni&ecirc;n v&agrave; phụ nữ c&oacute; thai. Phơi nhiễm chất nitrosamines li&ecirc;n quan tới ung thư phổi, mũi, thực quản, gan, tuyến tụy v&agrave; cổ tử cung. Phơi nhiễm chất aldehydes như formaldehyde c&oacute; thể g&acirc;y ung thư phổi v&agrave; mũi, ngo&agrave;i việc khiến cho phổi dễ bị nhạy cảm trước c&aacute;c nhiễm khuẩn. Phơi nhiễm carbon monoxide l&agrave;m giảm việc cung cấp oxy tới tim l&agrave;m tăng nguy cơ g&acirc;y huyết khối, bệnh tim v&agrave; đột quỵ. Phơi nhiễm acrolein g&oacute;p phần l&agrave;m tăng mảng b&aacute;m trong mạch m&aacute;u cũng như tạo huyết khối, tăng nguy cơ tim mạch v&agrave; đột quỵ. Phơi nhiễm cũng g&acirc;y tổn hại khả năng chống vi&ecirc;m của phổi.</span></span></span></span></p>\r\n', '/diemtin/068c8cf27035bf3123ba8c4b09934e66.jpg', '2022-12-01', NULL, NULL),
	(19, 1, 0, 'Trung tâm Y tế quận Thốt Nốt kiểm tra kết quả thực hiện các chỉ số hoạt động của Trạm Y tế năm 2022', '<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">Theo Quyết định số 167/QĐ-TTYTTN, ng&agrave;y 10/11/2022 của Trung t&acirc;m Y tế quận Thốt Nốt, từ ng&agrave;y 16/11 đến 18/11/2022, Trung t&acirc;m Y tế quận Thốt Nốt th&agrave;nh lập đo&agrave;n gi&aacute;m s&aacute;t hoạt động Trạm Y tế phường năm 2022 do BS.CKI Nguyễn Văn Lộc, Ph&oacute; Gi&aacute;m đốc Trung t&acirc;m Y tế l&agrave;m trưởng đo&agrave;n, c&aacute;c th&agrave;nh vi&ecirc;n tham gia gồm c&aacute;c trưởng, ph&oacute; khoa của Trung t&acirc;m Y tế, ph&uacute;c tra kết quả thực hiện c&aacute;c chỉ số hoạt động của 9 trạm y tế tr&ecirc;n địa b&agrave;n quận Thốt Nốt.</span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">Qua ba ng&agrave;y l&agrave;m việc, đo&agrave;n ph&uacute;c tra đ&atilde; kiểm tra thực tế tại 09 Trạm Y tế. Đo&agrave;n đ&atilde; chấm điểm việc thực hiện c&aacute;c chỉ số hoạt động như: c&aacute;c chỉ số hoạt động về chuy&ecirc;n m&ocirc;n y tế: truyền th&ocirc;ng - gi&aacute;o dục sức khỏe; ph&ograve;ng, chống HIV/AIDS; vệ sinh m&ocirc;i trường v&agrave; an to&agrave;n thực phẩm; c&ocirc;ng t&aacute;c kh&aacute;m bệnh, chữa bệnh, phục hồi chức năng v&agrave; y học cổ truyền; chăm s&oacute;c sức khỏe b&agrave; mẹ - trẻ em; d&acirc;n số - kế hoạch h&oacute;a gia đ&igrave;nh&hellip; v&agrave; nh&acirc;n lực; cơ sở hạ tầng; trang thiết bị; t&agrave;i ch&iacute;nh; bảo hiểm y tế.</span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">Qua đ&aacute;nh gi&aacute; của đo&agrave;n ph&uacute;c tra, hoạt động chăm s&oacute;c sức khỏe nh&acirc;n d&acirc;n được c&aacute;c địa phương quan t&acirc;m chỉ đạo t&iacute;ch cực. Cấp ủy v&agrave; UBND c&aacute;c phường c&oacute; quan t&acirc;m s&acirc;u sắc về c&ocirc;ng t&aacute;c y tế, c&oacute; đưa c&aacute;c chương tr&igrave;nh y tế v&agrave;o Nghị quyết của HĐND phường và Đảng ủy phường để hoạt động. Cơ sở vật chất một số trạm đ&atilde; xuống cấp, hiện đang x&acirc;y dựng mới trạm y tế phường Thuận Hưng v&agrave; chuẩn bị đầu năm 2023 khởi c&ocirc;ng trạm y tế phường Trung Nhứt v&agrave; n&acirc;ng cấp một số trạm cũ. Trang thiết bị, nh&acirc;n lực c&aacute;c Trạm Y tế c&ograve;n thiếu, c&ocirc;ng việc qu&aacute; tải so với nguồn nh&acirc;n lực hiện tại để thực hiện c&ocirc;ng t&aacute;c ph&ograve;ng chống dịch bệnh, kh&aacute;m chữa bệnh tại địa phương. B&ecirc;n cạnh đ&oacute;, c&aacute;c trạm cũng l&agrave;m tham mưu tốt với Cấp ủy, UBND phường, từ đ&oacute; c&aacute;c hoạt động đều đạt theo kế hoạch tr&ecirc;n giao. Tuy nhi&ecirc;n, về mặt kh&aacute;ch quan, tỷ lệ bệnh sốt xuất huyết - tay ch&acirc;n miệng c&ograve;n tăng cao so với c&ugrave;ng kỳ, m&ocirc; h&igrave;nh b&aacute;c sĩ gia đ&igrave;nh chưa cập nhật th&ocirc;ng tin đầy đủ v&agrave;o hồ sơ sức khỏe,&hellip;</span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">Kết quả đo&agrave;n c&ocirc;ng t&aacute;c đ&atilde; đ&aacute;nh gi&aacute;, chấm điểm cả 09 Trạm Y tế thuộc Trung t&acirc;m Y tế quận Thốt Nốt, trong đ&oacute; Trạm Y tế phường T&acirc;n Lộc cao điểm nhất 94,9/100đ; phường Trung Ki&ecirc;n thấp điểm nhất 84,3đ; c&aacute;c trạm c&ograve;n lại đều tr&ecirc;n 90/100đ</span></span></span></span></p>\r\n', '/diemtin/f1636772e4062486004abde8de47b295.jpg', '2022-12-01', NULL, NULL),
	(20, 1, 0, 'THÔNG TIN BÁO CHÍ: HIỆU QUẢ CỦA CÔNG TÁC TIÊM CHỦNG, THỜI GIAN VÀ HIỆU LỰC CỦA VẮC XIN PHÒNG COVID-19', '<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">Theo tổ chức y tế thế giới (WHO) những ng&agrave;y gần đ&acirc;y t&igrave;nh h&igrave;nh dịch COVID-19 c&oacute; diễn biến phức tạp tr&ecirc;n to&agrave;n cầu, số ca tử vong tăng khoảng 4% v&agrave; cảnh b&aacute;o c&oacute; nguy cơ sẽ trải qua l&agrave;n s&oacute;ng dịch mới.</span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">C&aacute;c nghi&ecirc;n cứu tr&ecirc;n thế giới đ&atilde; ghi nhận hiệu quả bảo vệ của mũi ti&ecirc;m cơ bản vắc xin ph&ograve;ng COVID-19 giảm dần theo thời gian trong v&ograve;ng 6 th&aacute;ng sau khi ti&ecirc;m v&agrave; trong điều kiện xuất hiện c&aacute;c biến chủng mới. Do vậy, việc ti&ecirc;m vắc xin ph&ograve;ng COVID-19 tăng cường mũi 3, mũi 4 sẽ l&agrave;m tăng hiệu lực bảo vệ cơ thể trước nguy cơ mắc COVID-19. Đồng thời việc ti&ecirc;m kết hợp c&aacute;c loại vắc-xin ph&ograve;ng COVID-19 kh&aacute;c nhau được cho l&agrave; an to&agrave;n v&agrave; hiệu quả. Những người bị suy giảm miễn dịch ở mức độ trung b&igrave;nh hoặc nặng n&ecirc;n được ưu ti&ecirc;n ti&ecirc;m một liều bổ sung vắc-xin ph&ograve;ng COVID-19 sau 1 đến 3 th&aacute;ng.</span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">Để tiếp tục duy tr&igrave; miễn dịch cộng đồng v&agrave; hạn chế thấp nhất c&aacute;c trường hợp mắc mới hoặc t&aacute;i nhiễm COVID-19. Ban Chỉ đạo ph&ograve;ng chống dịch COVID -19 th&agrave;nh phố Cần Thơ k&ecirc;u gọi mỗi người d&acirc;n, mỗi gia đ&igrave;nh c&ugrave;ng với ch&iacute;nh quyền địa phương v&agrave; c&aacute;c ban, ng&agrave;nh, đo&agrave;n thể tiếp tục hưởng ứng v&agrave; vận động mọi người c&ugrave;ng hưởng ứng chiến dịch ti&ecirc;m nhắc lại vắc xin COVID-19. Cụ thể:</span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">1. H&atilde;y đưa trẻ từ 5 &ndash; 18 tuổi đến c&aacute;c điểm ti&ecirc;m tại c&aacute;c trường học hoặc c&aacute;c trạm y tế phường, x&atilde; để ti&ecirc;m mũi 1 (nếu chưa ti&ecirc;m) v&agrave; ti&ecirc;m mũi 2 (đối với những ch&aacute;u đ&atilde; ti&ecirc;m mũi 1 v&agrave; đến l&uacute;c ti&ecirc;m mũi 2).</span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">2. Người d&acirc;n trong cộng đồng đ&atilde; ti&ecirc;m mũi 2 đến thời gian ti&ecirc;m mũi 3 v&agrave; người đ&atilde; ti&ecirc;m mũi 3 đến thời gian ti&ecirc;m mũi 4 h&atilde;y khẩn trương đến Trạm y tế gần nhất để được ti&ecirc;m nhắc lại.</span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">3. Trường đại học, Cao đẳng, Trung cấp tr&ecirc;n địa b&agrave;n th&agrave;nh phố chủ động r&agrave; so&aacute;t đối tượng, phối hợp y tế thực hiện ti&ecirc;m tại trường hoặc c&aacute;c trạm y tế phường x&atilde; để ti&ecirc;m nhắc lại.</span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:#666565"><span style="font-family:Verdana,Arial,Helvetica,sans-serif"><span style="background-color:#ffffff">Ban Chỉ đạo ph&ograve;ng chống dịch Covid -19 th&agrave;nh phố Cần Thơ y&ecirc;u cầu trung t&acirc;m Y tế quận, huyện, Trạm y tế c&aacute;c phường, x&atilde; tr&ecirc;n địa b&agrave;n th&agrave;nh phố bố tr&iacute; nh&acirc;n nh&acirc;n lực thực hiện ti&ecirc;m vắc xin trong th&aacute;ng cao điểm của th&agrave;nh phố đảm bảo hiệu quả v&agrave; an to&agrave;n nhất./.</span></span></span></span></p>\r\n', '/diemtin/2a367cd4af63e0715ecb34a26ba7b06b.jpg', '2022-12-01', NULL, NULL),
